    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        String senha = new String(txtSenha.getPassword());
        if (escolha.equals("Adicionar produto")) {
            if (txtLogin.getText().equals("verdoce") && senha.equals("coxinha123")) {
                ViewAddProduto va = new ViewAddProduto();
                va.setVisible(true);
                va.setLocationRelativeTo(null);
                dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Login ou Senha incorreto.1");
            }
        } else if (escolha.equals("Editar produto")) {
            if (txtLogin.getText().equals("verdoce") && senha.equals("coxinha123")) {
                ViewEditarProduto va = new ViewEditarProduto();
                va.setVisible(true);
                va.setLocationRelativeTo(null);
                dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Login ou Senha incorreto.2");
            }
        } else if (escolha.equals("Produto")) {
            if (txtLogin.getText().equals("verdoce") && senha.equals("coxinha123")) {
                ViewRemoveProduto va = new ViewRemoveProduto();
                va.setVisible(true);
                va.setLocationRelativeTo(null);
                dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Login ou Senha incorreto.3");
            }
        }

    }//GEN-LAST:event_jButton1ActionPerformed

